  SELECT
        po_id,
        po_ref,
        po_date,
        po_total_amount,
        status,
        supplier_name,
        address,
        contact_no1,
        contact_no2,
        contact_person,
        email_address
    FROM
        po,
        supplier
    WHERE
        po.supplier_id = supplier.supplier_id
;
