  CREATE OR REPLACE EDITIONABLE PACKAGE BODY "EXTRACTION_PKG" AS

    PROCEDURE set_up_stage_area IS
    BEGIN
        INSERT INTO xxbcm_order_mgt_stage (
            order_ref,
            order_date,
            supplier_name,
            supp_contact_name,
            supp_address,
            supp_contact_number,
            supp_email,
            order_total_amount,
            order_description,
            order_status,
            order_line_amount,
            invoice_reference,
            invoice_date,
            invoice_status,
            invoice_hold_reason,
            invoice_amount,
            invoice_description
        )
            SELECT
                order_ref,
                order_date,
                supplier_name,
                supp_contact_name,
                supp_address,
                supp_contact_number,
                supp_email,
                order_total_amount,
                order_description,
                order_status,
                order_line_amount,
                invoice_reference,
                invoice_date,
                invoice_status,
                invoice_hold_reason,
                invoice_amount,
                invoice_description
            FROM
                xxbcm_order_mgt
            MINUS
            SELECT
                order_ref,
                order_date,
                supplier_name,
                supp_contact_name,
                supp_address,
                supp_contact_number,
                supp_email,
                order_total_amount,
                order_description,
                order_status,
                order_line_amount,
                invoice_reference,
                invoice_date,
                invoice_status,
                invoice_hold_reason,
                invoice_amount,
                invoice_description
            FROM
                xxbcm_order_mgt_stage;

    END set_up_stage_area;

    PROCEDURE extract_supplier AS
        v_supplier_id VARCHAR2(2000);
    BEGIN
        FOR c IN (
            SELECT DISTINCT
                supplier_name,
                supp_contact_name,
                supp_address,
                supp_contact_number,
                CASE
                    WHEN regexp_replace(supp_contact_number, '[^0-9,]', '') NOT LIKE '%,%' THEN
                        NULL
                    ELSE
                        regexp_substr(regexp_replace(supp_contact_number, '[^0-9,]', ''), '[^,]+$')
                END contact2,
                regexp_substr(regexp_replace(supp_contact_number, '[^0-9,]', ''), '[^,]+') contact1,
                supp_email
            FROM
                xxbcm_order_mgt_stage
            WHERE
                order_ref NOT LIKE '%-%'
        ) LOOP
            BEGIN
                SELECT
                    supplier_id
                INTO v_supplier_id
                FROM
                    supplier
                WHERE
                    TRIM(initcap(supplier_name)) = TRIM(initcap(c.supplier_name));
            
            --assumption made that more recent PO will carry updated supplier information

                UPDATE supplier
                SET
                    address = c.supp_address,
                    contact_no1 = c.contact1,
                    contact_no2 = c.contact2,
                    contact_person = c.supp_contact_name,
                    email_address = c.supp_email
                WHERE
                    supplier_id = v_supplier_id;

            EXCEPTION
                WHEN no_data_found THEN
                    INSERT INTO supplier (
                        supplier_name,
                        address,
                        contact_no1,
                        contact_no2,
                        contact_person,
                        email_address
                    ) VALUES (
                        c.supplier_name,
                        c.supp_address,
                        c.contact1,
                        c.contact2,
                        c.supp_contact_name,
                        c.supp_email
                    );

            END;
        END LOOP;
    END extract_supplier;

    FUNCTION convert_to_number (
        p_amount IN VARCHAR2
    ) RETURN NUMBER IS
        v_number NUMBER;
    BEGIN
        v_number := replace(replace(replace(replace(upper(p_amount), ',', ''), 'S', '5'), 'O', '0'), 'I', '1');

        RETURN v_number;
    END convert_to_number;

    FUNCTION convert_to_date (
        p_date IN VARCHAR2
    ) RETURN DATE IS
        v_date DATE;
    BEGIN
        BEGIN
            SELECT
                to_date(p_date, 'DD-MON-YYYY')
            INTO v_date
            FROM
                dual;

        EXCEPTION
            WHEN OTHERS THEN
                SELECT
                    to_date(p_date, 'DD-MM-YYYY')
                INTO v_date
                FROM
                    dual;

        END;

        RETURN v_date;
    END convert_to_date;

    PROCEDURE extract_purchase_order IS
        v_supplier_id          NUMBER;
        v_order_date           DATE;
        v_order_total_amount   NUMBER;
        v_po_id                NUMBER;
    BEGIN
        FOR c IN (
            SELECT
                stage_id,
                order_ref,
                order_date,
                supplier_name,
                order_total_amount,
                order_description,
                order_status,
                po_status
            FROM
                xxbcm_order_mgt_stage
            WHERE
                order_ref NOT LIKE '%-%'
            ORDER BY
                order_ref
        ) LOOP
            IF nvl(c.po_status, '!') = 'OK' THEN
                CONTINUE;
            END IF;
            BEGIN
                SELECT
                    supplier_id
                INTO v_supplier_id
                FROM
                    supplier
                WHERE
                    TRIM(initcap(supplier_name)) = TRIM(initcap(c.supplier_name));

            EXCEPTION
                WHEN no_data_found THEN
                    UPDATE xxbcm_order_mgt_stage
                    SET
                        po_status = 'ERR',
                        err_msg = 'Supplier '
                                  || c.supplier_name
                                  || ' could not be found.'
                    WHERE
                        stage_id = c.stage_id;

                    CONTINUE;
            END;

            BEGIN
                v_order_date := convert_to_date(c.order_date);
            EXCEPTION
                WHEN OTHERS THEN
                    UPDATE xxbcm_order_mgt_stage
                    SET
                        po_status = 'ERR',
                        err_msg = 'Date '
                                  || c.order_date
                                  || ' is not in a proper date format'
                    WHERE
                        stage_id = c.stage_id;

                    CONTINUE;
            END;

            BEGIN
                v_order_total_amount := convert_to_number(c.order_total_amount);
            EXCEPTION
                WHEN OTHERS THEN
                    UPDATE xxbcm_order_mgt_stage
                    SET
                        po_status = 'ERR',
                        err_msg = 'Amount '
                                  || c.order_total_amount
                                  || ' is not in a proper numeric format'
                    WHERE
                        stage_id = c.stage_id;

                    CONTINUE;
            END;

            BEGIN
                SELECT
                    po_id
                INTO v_po_id
                FROM
                    po
                WHERE
                    po_ref = c.order_ref;

                UPDATE xxbcm_order_mgt_stage
                SET
                    po_status = 'ERR',
                    err_msg = 'PO '
                              || c.order_ref
                              || ' already exists.'
                WHERE
                    stage_id = c.stage_id;

                CONTINUE;
            EXCEPTION
                WHEN no_data_found THEN
                    INSERT INTO po (
                        po_ref,
                        po_date,
                        supplier_id,
                        po_total_amount,
                        description,
                        status
                    ) VALUES (
                        c.order_ref,
                        v_order_date,
                        v_supplier_id,
                        v_order_total_amount,
                        c.order_description,
                        c.order_status
                    );

            END;

            UPDATE xxbcm_order_mgt_stage
            SET
                po_status = 'OK'
            WHERE
                stage_id = c.stage_id;

        END LOOP;
    END extract_purchase_order;

    PROCEDURE extract_lines IS

        v_order_line_amount    NUMBER;
        v_po_id                NUMBER;
        v_po_line_id           NUMBER;
        v_max_po_line_number   NUMBER;
        v_po_line_number       NUMBER;
        v_invoice_id           NUMBER;
        v_invoice_date         DATE;
        v_invoice_amount       NUMBER;
        v_chk_po_line_number   NUMBER;
        v_invoice_line_id      NUMBER;
    BEGIN
        FOR c IN (
            SELECT
                stage_id,
                order_ref,
                regexp_substr(order_ref, '[^-]+') po_ref,
                regexp_substr(order_ref, '[^-]+$') po_line,
                order_description,
                order_status,
                order_line_amount,
                invoice_reference,
                invoice_date,
                invoice_status,
                invoice_hold_reason,
                invoice_amount,
                invoice_description,
                po_line_status,
                inv_status,
                invoice_line_status,
                order_line
            FROM
                xxbcm_order_mgt_stage
            WHERE
                order_ref LIKE '%-%'
            ORDER BY
                order_ref
        ) LOOP
            IF nvl(c.po_line_status, '!') = 'OK' THEN
            --fetching po_line_id to be used by invoice_line if invoice_line has not yet been loaded
                SELECT
                    po_line_id
                INTO v_po_line_id
                FROM
                    po_line
                WHERE
                    po_id = (
                        SELECT
                            po_id
                        FROM
                            po
                        WHERE
                            po_ref = c.po_ref
                    )
                    AND po_line_number = c.order_line;

            ELSE
           --create PO
                BEGIN
                    SELECT
                        po_id
                    INTO v_po_id
                    FROM
                        po
                    WHERE
                        po_ref = c.po_ref;

                EXCEPTION
                    WHEN no_data_found THEN
                        UPDATE xxbcm_order_mgt_stage
                        SET
                            po_line_status = 'ERR',
                            err_msg = 'PO '
                                      || c.po_ref
                                      || ' has not been found'
                        WHERE
                            stage_id = c.stage_id;

                        CONTINUE;
                END;

                BEGIN
                    v_order_line_amount := convert_to_number(c.order_line_amount);
                EXCEPTION
                    WHEN OTHERS THEN
                        UPDATE xxbcm_order_mgt_stage
                        SET
                            po_line_status = 'ERR',
                            err_msg = 'Amount '
                                      || c.order_line_amount
                                      || ' is not in a proper numeric format'
                        WHERE
                            stage_id = c.stage_id;

                        CONTINUE;
                END;

                BEGIN
                    SELECT
                        po_line_number
                    INTO v_chk_po_line_number
                    FROM
                        po_line
                    WHERE
                        po_id = v_po_id
                        AND po_line_number = c.po_line;

                    SELECT
                        MAX(po_line_number)
                    INTO v_max_po_line_number
                    FROM
                        po_line
                    WHERE
                        po_id = v_po_id;

                    v_po_line_number := v_max_po_line_number + 1;
                EXCEPTION
                    WHEN no_data_found THEN
                        v_po_line_number := to_number(c.po_line);
                END;

                UPDATE xxbcm_order_mgt_stage
                SET
                    order_line = v_po_line_number
                WHERE
                    stage_id = c.stage_id;

                SELECT
                    po_line_seq.NEXTVAL
                INTO v_po_line_id
                FROM
                    dual;

                INSERT INTO po_line (
                    po_line_id,
                    po_id,
                    po_line_description,
                    po_line_number,
                    po_line_status,
                    po_line_amount
                ) VALUES (
                    v_po_line_id,
                    v_po_id,
                    c.order_description,
                    v_po_line_number,
                    c.order_status,
                    v_order_line_amount
                );

                UPDATE xxbcm_order_mgt_stage
                SET
                    po_line_status = 'OK'
                WHERE
                    stage_id = c.stage_id;

            END IF;

            IF nvl(c.inv_status, '!') = 'OK' THEN
         --fetching invoice_id to be used by invoice_line if invoice_line has not yet been loaded
                SELECT
                    invoice_id
                INTO v_invoice_id
                FROM
                    invoice
                WHERE
                    invoice_reference = c.invoice_reference;

            ELSE
                IF c.invoice_reference IS NOT NULL THEN
               --creating invoice
                    BEGIN
                        SELECT
                            invoice_id
                        INTO v_invoice_id
                        FROM
                            invoice
                        WHERE
                            invoice_reference = c.invoice_reference;

                    EXCEPTION
                        WHEN no_data_found THEN
                            BEGIN
                                v_invoice_date := convert_to_date(c.invoice_date);
                            EXCEPTION
                                WHEN OTHERS THEN
                                    UPDATE xxbcm_order_mgt_stage
                                    SET
                                        inv_status = 'ERR',
                                        err_msg = 'Date '
                                                  || c.invoice_date
                                                  || ' is not in a proper date format'
                                    WHERE
                                        stage_id = c.stage_id;

                                    CONTINUE;
                            END;

                            SELECT
                                invoice_seq.NEXTVAL
                            INTO v_invoice_id
                            FROM
                                dual;

                            INSERT INTO invoice (
                                invoice_id,
                                invoice_reference,
                                invoice_date,
                                invoice_status,
                                invoice_hold_reason
                            ) VALUES (
                                v_invoice_id,
                                c.invoice_reference,
                                v_invoice_date,
                                c.invoice_status,
                                c.invoice_hold_reason
                            );

                            UPDATE xxbcm_order_mgt_stage
                            SET
                                inv_status = 'OK'
                            WHERE
                                stage_id = c.stage_id;

                    END;

                    IF nvl(c.invoice_line_status, '!') != 'OK' THEN
                  --creating invoice_line
                        BEGIN
                            v_invoice_amount := convert_to_number(c.invoice_amount);
                        EXCEPTION
                            WHEN OTHERS THEN
                                UPDATE xxbcm_order_mgt_stage
                                SET
                                    invoice_line_status = 'ERR',
                                    err_msg = 'Amount '
                                              || c.invoice_amount
                                              || ' is not in a proper numeric format'
                                WHERE
                                    stage_id = c.stage_id;

                                CONTINUE;
                        END;

                        BEGIN
                            SELECT
                                invoice_line_id                                                                                       -- kind of redundant
                            INTO v_invoice_line_id
                            FROM
                                invoice_line
                            WHERE
                                invoice_id = v_invoice_id
                                AND invoice_description = c.invoice_description
                                AND invoice_amount = v_invoice_amount;

                            CONTINUE;
                        EXCEPTION
                            WHEN no_data_found THEN
                                INSERT INTO invoice_line (
                                    invoice_id,
                                    invoice_amount,
                                    invoice_description,
                                    po_line_id
                                ) VALUES (
                                    v_invoice_id,
                                    v_invoice_amount,
                                    c.invoice_description,
                                    v_po_line_id
                                );

                                UPDATE xxbcm_order_mgt_stage
                                SET
                                    invoice_line_status = 'OK'
                                WHERE
                                    stage_id = c.stage_id;

                        END;

                    END IF;

                END IF;
            END IF;

        END LOOP;
    END extract_lines;

    PROCEDURE extraction AS
    BEGIN
        set_up_stage_area;
        extract_supplier;
        extract_purchase_order;
        extract_lines;
    END extraction;

END extraction_pkg;

