--  DDL for Function GET_INV_STATUS
--------------------------------------------------------

  CREATE OR REPLACE EDITIONABLE FUNCTION "GET_INV_STATUS" (
    p_po_id IN NUMBER
) RETURN VARCHAR2 IS
    v_paid      NUMBER;
    v_pending   NUMBER;
    v_unknown   NUMBER;
BEGIN
    SELECT
        COUNT(*)
    INTO v_paid
    FROM
        po_invoice_line_v
    WHERE
        po_id = p_po_id
        AND nvl(invoice_status, '!') = 'Paid';

    SELECT
        COUNT(*)
    INTO v_pending
    FROM
        po_invoice_line_v
    WHERE
        po_id = p_po_id
        AND nvl(invoice_status, '!') = 'Pending';

    SELECT
        COUNT(*)
    INTO v_unknown
    FROM
        po_invoice_line_v
    WHERE
        po_id = p_po_id
        AND invoice_status IS NULL;

    IF v_unknown > 0 THEN
        RETURN 'To verify';
    ELSIF v_pending > 0 THEN
        RETURN 'To follow up';
    ELSE
        RETURN 'OK';
    END IF;

END get_inv_status;

